package b4DruCare;

public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 1; i <=5; i++) { // row 
			for(int j = 1; j <=i; j++) { //columns
				System.out.print(i);
			}
			System.out.println();
			
		}
	}

}

