package b4DruCare;

public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int count = 1; count <=5; count++) { // row 
			for(int iterator = 1; iterator <=count; iterator++) { //columns
				System.out.print(count);
			}
			System.out.println();
			
		}
	}

}

